package stringPro;

public class IntToString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i1= 1234;
		int i2= 4321;
		System.out.println(i1+i2);
		String s1 = String.valueOf(i1);
		String s2 = String.valueOf(i2);
		System.out.println(s1+s2);
	}

}
