package stringPro;

public class StringToInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1= "1234";
		String s2= "4321";
		System.out.println(s1+s2);
		int i1 = Integer.parseInt(s1);
		int i2 = Integer.parseInt(s2);
		System.out.println(i1+i2);
	}

}
